.product-card__rating + .badge-dark, .product__rating + .product__out-of-stock {
font-size: 0px;
}
.product-card__rating + .badge-dark:before {
color: #fff;
background-color: #343a40;
content: 'Esgotado';
font-size: 1rem !important;
display: inline-block;
padding: .25em .4em;
font-weight: 700;
line-height: 1;
text-align: center;
white-space: nowrap;
vertical-align: baseline;
border-radius: .25rem;
}
.product__rating + .product__out-of-stock:before {
content: ‘Esgotado’;
font-weight: var(–font-bold);
font-size: var(–font-size-lg);
color: var(–warning);
}
.page-home .apx_faq {
  margin-top: -5rem
}
.page-home .banners-grid {
  margin-top: -5rem
}
.shipping-line[data-service-code="Retirada"] strong {
    font-size: 0;
}
.shipping-line[data-service-code="Retirada"] strong::after {
    font-size: 12px !important;
    content: 'Retirada disponível em até 2 horas';
}
.shipping-line[data-service-code="express"] strong {
    font-size: 0;
}
.shipping-line[data-service-code="express"] strong::after {
    font-size: 12px !important;
    content: 'Entrega em até 2 horas';
}
.shipping-line[data-service-code="PROPRIA-3"] strong {
    font-size: 0;
}
.shipping-line[data-service-code="PROPRIA-3"] strong::after {
    font-size: 12px !important;
    content: 'Ainda hoje!';
}
.shipping-line[data-service-code="PROPRIA-3"] + small:last-of-type {
    font-size: 0;
}

.shipping-line[data-service-code="PROPRIA-3"] + small:last-of-type::after {
    font-size: 12px !important;
    content: 'Homem raio ⚡';
    display: inline;
}
.shipping-line[data-service-code="Retirada"] span.mr-2 {
    font-size: 0;
}
.shipping-line[data-service-code="Retirada"] span.mr-2::after {
    font-size: 12px !important;
    content: 'Retirada na Houze';
  }
