body.is-loading #main {
	opacity: 0;
}

body #wrapper:after {
	background-position: center;
	background-repeat: no-repeat;
	background-size: contain;
	content: '';
	display: block;
	height: 6rem;
	left: calc(50% - 3rem);
	opacity: 0;
	pointer-events: none;
	position: fixed;
	top: calc(50% - 3rem);
	transition: opacity 1s ease, visibility 1s;
	visibility: hidden;
	width: 6rem;
	z-index: 100000;
}

body.is-loading {
	pointer-events: none;
}

body.is-loading.with-loader #wrapper:after {
	background-image: url('data:image/svg+xml;base64,');
	opacity: 1;
	transform: scale(1.0);
	visibility: visible;
}

body.is-playing.with-loader #wrapper:after {
	opacity: 0;
	transform: scale(0.5);
	transition: opacity 0.25s ease, transform 0.75s ease, visibility 0.25s;
	visibility: hidden;
}

#main > .inner > header, #main > .inner > footer {
	transition: opacity 0.375s ease-in-out 0.5625s,visibility 0.375s linear 0.5625s;
}

